mapper.update:
update tripboard
set title = #{title}, content = #{content}, uDate = now()
where num = #{num}

mapper.select:
select num, userID, nickname, title, content, DATE_FORMAT(wDate, '%Y-%m-%d') 'wDate', uDate, views, likeCount
from tripboard
where num = #{num}

mapper.update:
update tripboard
set views = views + 1
where num = #{num}

mapper.select:
select views
from tripboard
where num = #{num}

mapper.update:
update tripboard
set likeCount = likeCount + 1
where num = #{num}

update tripboard
set likeCount = likeCount - 1
where num = #{num}

mapper.select:
select likeCount
from tripboard
where num = #{num}
